package model.domain;import common.utils.UserUtils;import javax.persistence.Column;import javax.persistence.MappedSuperclass;import javax.persistence.PrePersist;import javax.persistence.PreUpdate;import java.sql.Timestamp;import java.util.Date;@MappedSuperclasspublic abstract class BaseEntity {    private Timestamp creatLa;    private String creatDe;    private Timestamp modificatLa;    private String modificatDe;    @Column(name = "modificat_la", insertable = false, updatable = true)    public Timestamp getModificatLa() {        return modificatLa;    }    void setModificatLa(Timestamp modificatLa) {        this.modificatLa = modificatLa;    }    @Column(name = "modificat_de", nullable = true, insertable = true, updatable = true, length = 150)    public String getModificatDe() {        return modificatDe;    }    void setModificatDe(String modificatDe) {        this.modificatDe = modificatDe;    }    @Column(name = "creat_la", insertable = true, updatable = false)    public Timestamp getCreatLa() {        return creatLa;    }    void setCreatLa(Timestamp creatLa) {        this.creatLa = creatLa;    }    @Column(name = "creat_de", nullable = false, insertable = true, updatable = true, length = 150)    public String getCreatDe() {        return creatDe;    }    void setCreatDe(String creatDe) {        this.creatDe = creatDe;    }    @PrePersist    void onCreate() {        this.setCreatLa(new Timestamp((new Date()).getTime()));        this.setCreatDe(UserUtils.getLoggedInUsername());    }    @PreUpdate    void onPersist() {        this.setModificatLa(new Timestamp((new Date()).getTime()));        this.setModificatDe(UserUtils.getLoggedInUsername());    }}